import { useState } from "react";

//Exemplo professor

const [peso, setPeso] = useState('');
const [altura, setAltura] = useState('');
const [resposta, setResposta] = useState('');

function Calculo (){
    const alturaMetros = parseFloat(altura);
    const pesoKg = parseFloat(peso);
    if (isNaN(pesoKg) || isNaN(alturaMetros)) {
        props.mensagemAlerta("Por favor, insira valores válidos!");
        return;
    }

    const imc = pesoKg / (alturaMetros * alturaMetros);
    return (
        <>
            <h3> Componente Calculo</h3>
            <h2>Resultado: {resposta}</h2>
            <div>
                <label>Numero 1</label>
                <input type="number" value={num1}
                    onChange={e => setPeso(e.target.value)} />
            </div>
            <div>
                <label>Numero 2</label>
                <input type="number" value={num2}
                    onChange={e => setAltura(e.target.value)} />
            </div>
            <button onClick={() => setResposta(imc)}>/</button>
        </>
    )
}

export default Calculo;
